import { makeAutoObservable } from "mobx";

export default class User {
	constructor() {
		this._isAuth = false
		this._user = {}
		makeAutoObservable(this)
	}

	setIsAuth(bool) {
		this._isAuth = bool
	}
	setUser(user) {
		this._user = user
	}

	get email() {
		return this._user.email
	}

	get phoneNumber() {
		return this._user.phoneNumber
	}

	get isAuth() {
		return this._isAuth
	}
	get user() {
		return this._user
	}
}
